    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String sql="insert into Account(Acc_no,C_name,DOB,Gender,Address,Nationality,Mob_no,MICR_no,IFSC,Acc_Type,Amount) values (?,?,?,?,?,?,?,?,?,?,?) "; 
       try{
          pst=conn.prepareStatement(sql); 
          pst.setString(1,jTextField1.getText());
          pst.setString(2,jTextField5.getText());
          pst.setString(3,((JTextField)jDateChooser1.getDateEditor().getUiComponent()).getText());
          jRadioButton3.setActionCommand("Male");
          jRadioButton4.setActionCommand("Female");
          pst.setString(4,buttonGroup1.getSelection().getActionCommand());
          pst.setString(5,jTextField6.getText());
          pst.setString(6,(String) jComboBox2.getSelectedItem());
          pst.setString(7,jTextField8.getText());
          pst.setString(8,jTextField2.getText());
          pst.setString(9,jTextField3.getText());
          pst.setString(10,(String) jComboBox1.getSelectedItem());
          pst.setString(11,jTextField7.getText());
          pst.execute();
          JOptionPane.showMessageDialog(null,"Account Successfully Created !!\n Thank You For Banking With Us");
          bal();
          setVisible(false);
          Profile_Page ob1=new Profile_Page();
          ob1.setVisible(true);
        } catch(Exception e){
           JOptionPane.showMessageDialog(null, "Please Enter All the Information..! ");
           
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed

